define(function(){$.fn.extend({autoLoadAddress:function(){$(this).focusout(function(){cep=$(this).val(),$.ajax({method:"GET",url:"http://api.postmon.com.br/v1/cep/"+cep,dataType:"json",beforeSend:function(){$(".iconLoadCep").removeClass("hide")},complete:function(){$(".iconLoadCep").addClass("hide")},success:function(o){$("#callback").empty(),$("#logradouro").val(o.logradouro),$("#bairro").val(o.bairro),$("#cidade").val(o.cidade),$("#pais").val("Brasil");var e=o.estado_info.nome.toUpperCase().replace(" ","_");$("#estado").select2("val",[e,o.estado_info.nome])},error:function(o,e,a){$("#callback").text("Erro ao chamar o serviço de verificação de CEP")}})})}})});