define(["ajax","api"],function(t,e){$(function(){$("#insertHeader").load("../../fragmentos/menu-navegacao.html"),$("#busca-produto").select2({ajax:{url:e["pdv.pesquisa.produto"],dataType:"json",processResults:function(t,e){return{results:$.map(t,function(t){return t.id=t.codigoBarra,t.text=t.descricao,t})}},cache:!1},placeholder:"Buscar Produtos",escapeMarkup:function(t){return t},minimumInputLength:1,templateResult:function(t){if(t.loading)return t.text;return"<div class='row'><div class='col-sm-1'><img src='"+t.imagem+"' width='75px'></div><div class='col-sm-11'><span class='select2-produto'>"+t.descricao+"</span><br><span class='select2-info text-muted'><strong>Categoria:</strong> "+t.categoria+"</span><span class='select2-info text-muted'><strong>Marca:</strong> "+t.marca+"</span><span class='select2-info text-muted'><strong>Unidade:</strong> "+t.medida+"</span></div></div>"},templateSelection:function(t){return t.descricao||t.text}})})});